import React, { useState } from 'react';
import API from '../api';

export default function UploadPage() {
  const [file,setFile]=useState(null);
  const [patientAddr,setPatientAddr]=useState('');
  const [recordId,setRecordId]=useState('');

  const handleUpload=async(e)=>{
    e.preventDefault();
    const fd=new FormData();
    fd.append('file',file);
    fd.append('patientAddress',patientAddr);
    fd.append('recordId',recordId);
    const res=await API.post('/records/upload',fd,{headers:{'Content-Type':'multipart/form-data'}});
    alert('Uploaded CID: '+res.data.cid+'\nSymKey(hex): '+res.data.symKeyHex);
  }

  return (
    <div>
      <h2>Upload Encrypted Medical Record</h2>
      <form onSubmit={handleUpload}>
        <input placeholder="Patient Address" value={patientAddr} onChange={e=>setPatientAddr(e.target.value)} /><br/><br/>
        <input placeholder="Record ID" value={recordId} onChange={e=>setRecordId(e.target.value)} /><br/><br/>
        <input type="file" onChange={e=>setFile(e.target.files[0])} /><br/><br/>
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}
