import React,{useState} from 'react';
import API from '../api';

export default function GrantPage(){
  const [consentId,setConsentId]=useState('');
  const [grantee,setGrantee]=useState('');
  const [recordId,setRecordId]=useState('');
  const [validUntil,setValidUntil]=useState(Math.floor(Date.now()/1000)+7*24*3600);

  const handleGrant=async(e)=>{
    e.preventDefault();
    const res=await API.post('/records/grant',{consentId, granteeAddress: grantee, recordId, validUntil});
    alert('Consent Granted! TX Hash: '+res.data.txHash);
  }

  return (
    <div>
      <h2>Grant Consent</h2>
      <form onSubmit={handleGrant}>
        <input placeholder="Consent ID" value={consentId} onChange={e=>setConsentId(e.target.value)} /><br/><br/>
        <input placeholder="Grantee Address" value={grantee} onChange={e=>setGrantee(e.target.value)} /><br/><br/>
        <input placeholder="Record ID" value={recordId} onChange={e=>setRecordId(e.target.value)} /><br/><br/>
        <input placeholder="Valid Until (Unix timestamp)" value={validUntil} onChange={e=>setValidUntil(e.target.value)} /><br/><br/>
        <button type="submit">Grant Consent</button>
      </form>
    </div>
  );
}
