import React, { useState } from 'react';
import API from '../api';
import { ethers } from 'ethers';
import './Page.css';

export default function GrantConsentPage() {
  const [recordId, setRecordId] = useState('');
  const [clinicianAddr, setClinicianAddr] = useState('');

  const handleGrant = async (e) => {
    e.preventDefault();
    if (!recordId) return alert("Record ID missing");
    if (!clinicianAddr || !ethers.utils.isAddress(clinicianAddr))
      return alert("Invalid clinician Ethereum address");

    try {
      const res = await API.post('/records/grant', { recordId, clinicianAddress: clinicianAddr });
      alert("Consent granted!");
    } catch (err) {
      console.error(err);
      alert("Grant failed: " + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="page-container">
      <h2>Grant Access Consent</h2>
      <form className="form-container" onSubmit={handleGrant}>
        <input type="text" placeholder="Record ID" value={recordId}
               onChange={e => setRecordId(e.target.value)} />
        <input type="text" placeholder="Clinician Ethereum Address" value={clinicianAddr}
               onChange={e => setClinicianAddr(e.target.value.trim())} />
        <button type="submit">Grant Consent</button>
      </form>
    </div>
  );
}
