import React,{useState} from 'react';
import API from '../api';
export default function GrantConsent(){
  const [consentId,setConsentId]=useState('');
  const [grantee,setGrantee]=useState('');
  const [recordId,setRecordId]=useState('');
  const [validUntil,setValidUntil]=useState(Math.floor(Date.now()/1000)+7*24*3600);
  const handleGrant=async(e)=>{
    e.preventDefault();
    const res=await API.post('/records/grant',{consentId,granteeAddress:grantee,recordId,validUntil});
    alert('Granted TX:'+res.data.txHash);
  }
  return (<form onSubmit={handleGrant}>
    <div>Consent ID: <input value={consentId} onChange={e=>setConsentId(e.target.value)}/></div>
    <div>Grantee Address: <input value={grantee} onChange={e=>setGrantee(e.target.value)}/></div>
    <div>Record ID: <input value={recordId} onChange={e=>setRecordId(e.target.value)}/></div>
    <div>Valid Until (Unix): <input value={validUntil} onChange={e=>setValidUntil(e.target.value)}/></div>
    <button type="submit">Grant Consent</button>
  </form>);
}
