import React, {useState} from 'react';
import API from '../api';
export default function UploadRecord(){
  const [file,setFile]=useState(null);
  const [patientAddr,setPatientAddr]=useState('');
  const [recordId,setRecordId]=useState('');
  const handleUpload=async(e)=>{
    e.preventDefault();
    const fd=new FormData();
    fd.append('file',file);
    fd.append('patientAddress',patientAddr);
    fd.append('recordId',recordId);
    const res=await API.post('/records/upload',fd,{headers:{'Content-Type':'multipart/form-data'}});
    alert('Uploaded CID:'+res.data.cid+'\nSymKey(hex):'+res.data.symKeyHex);
  }
  return (<form onSubmit={handleUpload}>
    <div>Patient Address: <input value={patientAddr} onChange={e=>setPatientAddr(e.target.value)}/></div>
    <div>Record ID: <input value={recordId} onChange={e=>setRecordId(e.target.value)}/></div>
    <div><input type="file" onChange={e=>setFile(e.target.files[0])}/></div>
    <button type="submit">Upload</button>
  </form>);
}
