import React, { useState } from 'react';
import API from '../api';

export default function GrantConsent() {
  const [consentId, setConsentId] = useState('');
  const [grantee, setGrantee] = useState('');
  const [recordId, setRecordId] = useState('');
  const [validUntil, setValidUntil] = useState(Math.floor(Date.now() / 1000) + 7*24*3600);

  const handleGrant = async (e) => {
    e.preventDefault();
    if (!consentId || !grantee || !recordId) {
      alert("Please fill all fields");
      return;
    }
    try {
      const res = await API.post('/records/grant', { consentId, granteeAddress: grantee, recordId, validUntil });
      alert('Consent Granted! TX Hash: ' + res.data.txHash);
    } catch (err) {
      alert("Grant failed: " + err.message);
    }
  };

  return (
    <form onSubmit={handleGrant}>
      <div>
        <label>Consent ID:</label>
        <input value={consentId} onChange={e => setConsentId(e.target.value)} />
      </div>
      <div>
        <label>Grantee Address:</label>
        <input value={grantee} onChange={e => setGrantee(e.target.value)} />
      </div>
      <div>
        <label>Record ID:</label>
        <input value={recordId} onChange={e => setRecordId(e.target.value)} />
      </div>
      <div>
        <label>Valid Until (Unix timestamp):</label>
        <input value={validUntil} onChange={e => setValidUntil(e.target.value)} />
      </div>
      <button type="submit">Grant Consent</button>
    </form>
  );
}
