import React, { useState } from 'react';
import API from '../api';

export default function DownloadRecord() {
  const [recordId, setRecordId] = useState('');
  const [symKeyHex, setSymKeyHex] = useState('');

  const handleDownload = async (e) => {
    e.preventDefault();
    if (!recordId || !symKeyHex) {
      alert("Fill recordId and symmetric key (demo only)");
      return;
    }

    try {
      const res = await API.get(`/records/record/${recordId}`);
      const cid = res.data.cid;
      const response = await fetch(`http://127.0.0.1:8080/ipfs/${cid}`);
      const blob = await response.arrayBuffer();
      const key = new Uint8Array(Buffer.from(symKeyHex, 'hex'));

      // Decrypt AES-GCM
      const iv = blob.slice(0, 12);
      const tag = blob.slice(12, 28);
      const ciphertext = blob.slice(28);

      const cryptoKey = await window.crypto.subtle.importKey(
        'raw', key, { name: 'AES-GCM' }, false, ['decrypt']
      );

      const decrypted = await window.crypto.subtle.decrypt(
        { name: 'AES-GCM', iv: new Uint8Array(iv), tagLength: 128 },
        cryptoKey,
        ciphertext
      );

      const fileBlob = new Blob([decrypted]);
      const url = URL.createObjectURL(fileBlob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${recordId}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch (err) {
      alert("Download/decrypt failed: " + err.message);
    }
  };

  return (
    <form onSubmit={handleDownload}>
      <div>
        <label>Record ID:</label>
        <input value={recordId} onChange={e => setRecordId(e.target.value)} />
      </div>
      <div>
        <label>Symmetric Key (hex, demo):</label>
        <input value={symKeyHex} onChange={e => setSymKeyHex(e.target.value)} />
      </div>
      <button type="submit">Download & Decrypt</button>
    </form>
  );
}
