import React, { useState } from 'react';
import API from '../api';
import { ethers } from 'ethers';

export default function UploadRecord() {
  const [file, setFile] = useState(null);
  const [patientAddr, setPatientAddr] = useState('');
  const [recordId, setRecordId] = useState('');

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) return alert("Please select a file");
    if (!patientAddr || !ethers.utils.isAddress(patientAddr))
      return alert("Invalid Ethereum address");
    if (!recordId || recordId.length === 0) return alert("Record ID missing");

    const fd = new FormData();
    fd.append('file', file);
    fd.append('patientAddress', patientAddr);
    fd.append('recordId', recordId);

    try {
      const res = await API.post('/records/upload', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(`File uploaded successfully!\nCID: ${res.data.cid}\nSymKey (hex, demo only): ${res.data.symKeyHex}`);
    } catch (err) {
      console.error(err);
      alert("Upload failed: " + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div>
      <h2>Upload Medical Record</h2>
      <form onSubmit={handleUpload}>
        <div>
          <label>Patient Address:</label>
          <input value={patientAddr} onChange={e => setPatientAddr(e.target.value)} placeholder="0x..." />
        </div>
        <div>
          <label>Record ID:</label>
          <input value={recordId} onChange={e => setRecordId(e.target.value)} placeholder="Short identifier" />
        </div>
        <div>
          <input type="file" onChange={e => setFile(e.target.files[0])} />
        </div>
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}
